CREATE TABLE BET (ID BIGINT NOT NULL, BET_DATE DATETIME, BET_PRICE DOUBLE PRECISION, IS_WINNER BIT NOT NULL, MAIL VARCHAR(255), ROUND_OF_BET_ID BIGINT, TELEGRAM VARCHAR(255), USER VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE ROUND_OF_BET (ID BIGINT NOT NULL, DURATION_FOR_BETS_IN_SECONDS INTEGER, DURATION_TIME_IN_SECONDS INTEGER, FINAL_BTC_PRICE DOUBLE PRECISION, FINISH_DATE DATETIME, INITIAL_BTC_PRICE DOUBLE PRECISION, INITIAL_DATE DATETIME, STATUS VARCHAR(255), PRIMARY KEY (ID));
CREATE TABLE WINNER (ID BIGINT NOT NULL, BET_ID BIGINT, MAIL VARCHAR(255), TELEGRAM VARCHAR(255), USER VARCHAR(255), WINNER_DATE DATETIME, PRIMARY KEY (ID));
